import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
  overflow: hidden;
`;

const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) => props.direction == "left" && "10px"};
  right: ${(props) => props.direction == "right" && "10px"};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
`;

const Wrapper = styled.div`
  height: 100%;
`;

const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
`;

const ImageContainer = styled.div`
  flex: 1;
  height: 100%;
`;

const Image = styled.img`
  height: 80%;
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Title = styled.div``;
const Desc = styled.div``;
const Button = styled.div``;

function Slider() {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowLeftOutlined />
      </Arrow>
      <Wrapper>
        <Slide>
          <ImageContainer>
            <Image src="https://media.douglas.de/medias/tYdCnW697827-0-dgl-DE.jpg?context=bWFzdGVyfGltYWdlc3wxMDMyMjh8aW1hZ2UvanBlZ3xoODAvaGNhLzg4MjA5NDc4MTIzODIvdFlkQ25XNjk3ODI3XzBfZGdsLURFLmpwZ3xmZDNmZTFkZTY4NTdkNzg4ZTBiZGNmMTg5YjUxM2ZkZGMzNjc1NGZlNjI0MzRkOGNkM2JhNGVkZWQ0NjU3MTU0&imwidth=550" />
          </ImageContainer>
          <InfoContainer>

          </InfoContainer>
        </Slide>
      </Wrapper>
      <Arrow direction="right">
        <ArrowRightOutlined />
      </Arrow>
    </Container>
  );
}

export default Slider;
