import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
  overflow: hidden;
`;

const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) => props.direction == "left" && "10px"};
  right: ${(props) => props.direction == "right" && "10px"};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
  z-index: 2;
`;

const Wrapper = styled.div`
  height: 100%;
  display: flex;
`;

const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
  background-color: #${props=>props.bg}
`;

const ImageContainer = styled.div`
  height: 100%;
  flex: 1;
`;

const Image = styled.img`
  height: 80%;
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Title = styled.h1`
  font-size: 70px;
`;
const Desc = styled.p`
  margin: 50px 0px;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
`;
const Button = styled.button`
  padding: 10px;
  font-size: 20px;
  background-color: transparent;
  cursor: pointer;
`;

function Slider() {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowLeftOutlined />
      </Arrow>
      <Wrapper>
        <Slide bg="f4fafd">
          <ImageContainer>
            <Image src="https://media.douglas.de/medias/tYdCnW697827-0-dgl-DE.jpg?context=bWFzdGVyfGltYWdlc3wxMDMyMjh8aW1hZ2UvanBlZ3xoODAvaGNhLzg4MjA5NDc4MTIzODIvdFlkQ25XNjk3ODI3XzBfZGdsLURFLmpwZ3xmZDNmZTFkZTY4NTdkNzg4ZTBiZGNmMTg5YjUxM2ZkZGMzNjc1NGZlNjI0MzRkOGNkM2JhNGVkZWQ0NjU3MTU0&imwidth=550" />
          </ImageContainer>
          <InfoContainer>
            <Title>SUMMER SAL</Title>
            <Desc>DONT COMPRISE STYLE! GET FLAT 30% OFF FOR NEW SALE.</Desc>
            <Button>SHOW NOW</Button>
          </InfoContainer>
        </Slide>

        <Slide bg="fbf0f4">
          <ImageContainer>
            <Image src="https://media.douglas.de/medias/tYdCnW697827-0-dgl-DE.jpg?context=bWFzdGVyfGltYWdlc3wxMDMyMjh8aW1hZ2UvanBlZ3xoODAvaGNhLzg4MjA5NDc4MTIzODIvdFlkQ25XNjk3ODI3XzBfZGdsLURFLmpwZ3xmZDNmZTFkZTY4NTdkNzg4ZTBiZGNmMTg5YjUxM2ZkZGMzNjc1NGZlNjI0MzRkOGNkM2JhNGVkZWQ0NjU3MTU0&imwidth=550" />
          </ImageContainer>
          <InfoContainer>
            <Title>Winter SALE</Title>
            <Desc>DONT COMPRISE STYLE! GET FLAT 30% OFF FOR NEW SALE.</Desc>
            <Button>SHOW NOW</Button>
          </InfoContainer>
        </Slide>

        <Slide bg="fcf1ed">
          <ImageContainer>
            <Image src="https://media.douglas.de/medias/tYdCnW697827-0-dgl-DE.jpg?context=bWFzdGVyfGltYWdlc3wxMDMyMjh8aW1hZ2UvanBlZ3xoODAvaGNhLzg4MjA5NDc4MTIzODIvdFlkQ25XNjk3ODI3XzBfZGdsLURFLmpwZ3xmZDNmZTFkZTY4NTdkNzg4ZTBiZGNmMTg5YjUxM2ZkZGMzNjc1NGZlNjI0MzRkOGNkM2JhNGVkZWQ0NjU3MTU0&imwidth=550" />
          </ImageContainer>
          <InfoContainer>
            <Title>POPULAR SALE</Title>
            <Desc>DONT COMPRISE STYLE! GET FLAT 30% OFF FOR NEW SALE.</Desc>
            <Button>SHOW NOW</Button>
          </InfoContainer>
        </Slide>
      </Wrapper>

      <Arrow direction="right">
        <ArrowRightOutlined />
      </Arrow>
    </Container>
  );
}

export default Slider;
