import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
  overflow: hidden;
  background: coral;
`;

const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) => props.direction == "left" && "10px"};
  right: ${(props) => props.direction == "right" && "10px"};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
`;

const Wrapper = styled.div`
  height: 100%;
`;

const Slide = styled.div`
width: 100vh;
height: 
  display: flex;
  align-items: center;
`;

const ImageContainer = styled.div`
  flex: 1;
`;

const Image = styled.div`
    height:80%;
`;

const InfoContainer = styled.div`
  flex: 1;
`;

function Slider() {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowLeftOutlined />
      </Arrow>
      <Wrapper>
          <Slide>
              <ImageContainer>
                  <Image src="https://z.nooncdn.com/products/tr:n-t_240/v1559566278/N26407420A_1.jpg"/>
              </ImageContainer>
          </Slide>
      </Wrapper>
      <Arrow direction="right">
        <ArrowRightOutlined />
      </Arrow>
    </Container>
  );
}

export default Slider;
