import styled from "styled-components";

const Container = styled.div``
function CategoryItem({item}) {
    return (
        <Container>
            <Image>{item.img}</Image>
            <Info>
                <Title>{item.title}</Title>
                <Button>SHOW NOW</Button>
            </Info>
        </Container>
    )
}

export default CategoryItem
